package com.example.news.component.payimport android.text.TextUtils/** * 支付宝支付结果解析类 * 目的是将Map参数解析到对象 * 可以不用该类 * 该类来自支付宝支付Demo */class PayResult(rawResult: Map<String, String>) {    /**     * @return the resultStatus     */    var resultStatus: String? = null    /**     * @return the result     */    var result: String? = null    /**     * @return the memo     */    var memo: String? = null    init {        if (rawResult != null) {            for (key in rawResult!!.keys) {                if (TextUtils.equals(key, "resultStatus")) {                    resultStatus = rawResult[key]                } else if (TextUtils.equals(key, "result")) {                    result = rawResult[key]                } else if (TextUtils.equals(key, "memo")) {                    memo = rawResult[key]                }            }        }    }    override fun toString(): String {        return ("resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}")    }}